import React from 'react';
import add from './assets/add.svg';
import list from './assets/list.svg';
import report_progress from './assets/progress.svg';
import './App.css';
import Carousel, { Dots } from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <div className= "App-top">
          <h1 className="App-name"> Simple OKRs </h1>
        </div>
        <div className="App-main">
        <h1> OKRs Made Simple </h1>
        <p1> Simple OKR - a lightweight and free slack app for OKR management.  </p1>
        <div className="summary">
          <p1 className="summary-header"> Summary </p1>
          <ul>
            <li>Add and manage personal, team, or company OKRs </li>
            <li>Add progress reports and view progress over time</li>
            <li>Share OKRs across your organization</li>
          </ul>
        </div>
        <div className="slack-button">
          <a href="https://slack.com/oauth/v2/authorize?client_id=875210016705.885000441780&scope=calls:read,calls:write,channels:read,chat:write,commands,dnd:read,groups:read,im:history,im:read,im:write,mpim:history,mpim:read,mpim:write,remote_files:read,remote_files:share,remote_files:write,team:read,users:read,users:read.email,users:write&user_scope=chat:write">
            <img alt="Add to Slack" height="40" width="139" src="https://platform.slack-edge.com/img/add_to_slack.png" srcset="https://platform.slack-edge.com/img/add_to_slack.png 1x, https://platform.slack-edge.com/img/add_to_slack@2x.png 2x"></img>
          </a>
        </div>
        <Carousel
          autoPlay={2000}
          animationSpeed={1000}
          slidesPerScroll={1}
          centered
          offset={0}
          infinite
        >
          <img src={add} />
          <img src={list} />
          <img src={report_progress} />
        </Carousel>
        <div className="tutorial"> 
          <p1> After adding Simple OKRs to your workplace, you can add OKRs from any channel with <code className="code">/okr add</code>.</p1>
        </div>
      </div>
      </header>
    </div>
  );
}

export default App;
